import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Payments() {
    // 状态管理
    const [showAccountDetails, setShowAccountDetails] = useState(false);
    const [showDepositDetails, setShowDepositDetails] = useState(false);
    const [showPinEntry, setShowPinEntry] = useState(false);
    const [showDepositPinEntry, setShowDepositPinEntry] = useState(false);
    const [bsb, setBsb] = useState('');
    const [accountNumber, setAccountNumber] = useState('');
    const [pin, setPin] = useState('');
    const [amount, setAmount] = useState('');
    const [animatePinEntry, setAnimatePinEntry] = useState(false);
    const [depositAmount, setDepositAmount] = useState('');
    const [depositPin, setDepositPin] = useState('');

    const navigate = useNavigate();

    // 辅助函数
    const isContinueEnabled = bsb.replace(/\s+/g, '').length === 6 && accountNumber.replace(/\s+/g, '').length === 9;

    const formatNumberWithSpaces = (value) => {
        return value.replace(/\D/g, '') // 移除非数字字符
            .replace(/(\d{3})(?=\d)/g, '$1 '); // 每三位添加一个空格
    };

    const handleBsbChange = (e) => {
        const formattedValue = formatNumberWithSpaces(e.target.value);
        setBsb(formattedValue);
    };

    const handleAccountNumberChange = (e) => {
        const formattedValue = formatNumberWithSpaces(e.target.value);
        setAccountNumber(formattedValue);
    };

    const handleAmountChange = (e) => {
        setAmount(e.target.value);
    };

    const handlePinInput = (num) => {
        if (pin.length < 6) {
            setPin((prevPin) => prevPin + num);
        }
    };

    const handleDelete = () => {
        setPin((prevPin) => prevPin.slice(0, -1));
    };

    const handleCancel = () => {
        setAnimatePinEntry(false);
        setTimeout(() => setShowPinEntry(false), 300);
    };

    const handleFooterClick = (path) => {
        navigate(path);
    };

    const handlePayClick = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            return;
        }

        try {
            const response = await axios.post(
                'http://localhost:5128/api/TransferRequest/create',
                {
                    BSB: bsb.replace(/\s+/g, ''),
                    AccountNumber: accountNumber.replace(/\s+/g, ''),
                    Amount: parseFloat(amount),
                    Pin: pin,
                },
                {
                    headers: {
                        Authorization: token,
                    },
                }
            );

            if (response.status === 200) {
                alert('Transfer request created successfully!');
            }
        } catch (error) {
            console.error('Error creating transfer request:', error);
            alert('Failed to create transfer request. Please check your inputs and try again.');
        } finally {
            setPin('');
            setShowPinEntry(false);
            setAnimatePinEntry(false);
        }
    };

    // 新增的存款功能函数
    const handleDepositClick = () => {
        setShowDepositDetails(true);
        setShowAccountDetails(false);
    };

    const handleDepositAmountChange = (e) => {
        const value = e.target.value;
        const regex = /^\d*\.?\d{0,2}$/;
        if (regex.test(value) || value === '') {
            setDepositAmount(value);
        }
    };

    const handleDepositContinue = () => {
        if (depositAmount && parseFloat(depositAmount) > 0) {
            setShowDepositPinEntry(true);
            setShowDepositDetails(false);
        } else {
            alert('Please enter a valid amount.');
        }
    };

    const handleDepositPinInput = (num) => {
        if (depositPin.length < 6) {
            setDepositPin((prevPin) => prevPin + num);
        }
    };

    const handleDepositPinDelete = () => {
        setDepositPin((prevPin) => prevPin.slice(0, -1));
    };

    const handleDepositPinCancel = () => {
        setShowDepositPinEntry(false);
        setDepositPin('');
    };

    const handleDepositSubmit = async () => {
        if (depositPin.length !== 6) {
            alert('Please enter a 6-digit PIN.');
            return;
        }

        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            return;
        }

        try {
            const response = await axios.post(
                'http://localhost:5128/api/TransferRequest/deposit',
                {
                    Amount: parseFloat(depositAmount),
                    Pin: depositPin,
                },
                {
                    headers: {
                        Authorization: token,
                    },
                }
            );

            if (response.status === 200) {
                alert('Deposit successful!');
                setShowDepositPinEntry(false);
                setDepositPin('');
                setDepositAmount('');
            }
        } catch (error) {
            console.error('Error during deposit:', error);
            alert('Failed to make deposit. Please try again.');
        }
    };

    // 样式
    const styles = {
        container: {
            backgroundColor: '#f4f4f4',
            minHeight: '100vh',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            width: '100%',
            boxSizing: 'border-box',
            padding: 0,
            overflowX: 'hidden',
        },
        header: {
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            width: '100%',
            marginBottom: '20px',
            boxSizing: 'border-box',
            padding: '10px',
        },
        headerText: {
            fontSize: '24px',
            fontWeight: 'bold',
        },
        quickActionsContainer: {
            display: 'flex',
            justifyContent: 'space-around',
            width: '60%',
            marginBottom: '20px',
        },
        quickActionButton: {
            backgroundColor: '#007bff',
            color: '#fff',
            padding: '15px 30px',
            borderRadius: '25px',
            textAlign: 'center',
            fontSize: '18px',
            cursor: 'pointer',
            flex: 1,
            margin: '0 10px',
        },
        formGroup: {
            marginBottom: '15px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'flex-start',
        },
        formLabel: {
            marginBottom: '5px',
            fontSize: '16px',
            fontWeight: 'bold',
        },
        formInput: {
            width: 'calc(100% - 40px)',
            padding: '10px',
            fontSize: '16px',
            borderRadius: '5px',
            border: '1px solid #ccc',
            boxSizing: 'border-box',
        },
        continueButton: {
            width: '30%',
            padding: '15px',
            borderRadius: '25px',
            backgroundColor: '#007bff',
            textAlign: 'center',
            fontSize: '18px',
            color: '#fff',
            cursor: 'pointer',
            marginTop: '20px',
            marginLeft: 'auto',
            marginRight: 'auto',
            display: 'block',
        },
        accountDetailsContainer: {
            position: 'fixed',
            bottom: '0',
            left: 0,
            width: '100%',
            height: '80%',
            backgroundColor: '#fff',
            borderTopLeftRadius: '20px',
            borderTopRightRadius: '20px',
            boxShadow: '0 -2px 8px rgba(0, 0, 0, 0.1)',
            transition: 'bottom 0.3s ease',
            padding: '20px',
            zIndex: 1000,
        },
        accountDetailsHeader: {
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '20px',
        },
        backButton: {
            fontSize: '24px',
            color: '#007bff',
            cursor: 'pointer',
            flexShrink: 0,
        },
        accountDetailsTitle: {
            fontSize: '20px',
            fontWeight: 'bold',
            textAlign: 'center',
            flex: 1,
        },
        footer: {
            display: 'flex',
            justifyContent: 'space-around',
            width: '100%',
            backgroundColor: '#fff',
            padding: '10px 0',
            boxShadow: '0 -2px 8px rgba(0, 0, 0, 0.1)',
            marginTop: 'auto',
            boxSizing: 'border-box',
        },
        footerItem: {
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '12px',
            cursor: 'pointer',
        },
        footerIcon: {
            fontSize: '24px',
            color: '#007bff',
            marginBottom: '5px',
        },
    };

    // PinEntry 组件
    function PinEntry() {
        const styles = {
            container: {
                backgroundColor: '#dcdcdc',
                height: '60vh',
                width: '100vw',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                borderRadius: '20px 20px 0 0',
                padding: '20px',
                position: 'fixed',
                bottom: animatePinEntry ? '0' : '-100%',
                left: '0',
                transition: 'bottom 0.3s ease',
            },
            header: {
                fontSize: '24px',
                fontWeight: 'bold',
                marginBottom: '30px',
            },
            pinDots: {
                display: 'flex',
                justifyContent: 'center',
                marginBottom: '30px',
            },
            dot: (isActive) => ({
                width: '10px',
                height: '10px',
                borderRadius: '50%',
                backgroundColor: isActive ? '#007bff' : '#000',
                margin: '0 5px',
            }),
            keypad: {
                display: 'grid',
                gridTemplateColumns: 'repeat(3, 1fr)',
                gap: '15px',
            },
            key: {
                width: '60px',
                height: '60px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                backgroundColor: '#e0e0e0',
                borderRadius: '50%',
                fontSize: '18px',
                cursor: 'pointer',
                transition: 'background-color 0.2s',
            },
            keyActive: {
                backgroundColor: '#cccccc',
            },
            footer: {
                display: 'flex',
                justifyContent: 'space-between',
                width: '100%',
                marginTop: '20px',
            },
            footerButton: {
                fontSize: '16px',
                color: '#007bff',
                cursor: 'pointer',
                textAlign: 'center',
                width: '33%',
            },
        };

        const [activeKey, setActiveKey] = useState(null);

        const handleKeyClick = (num) => {
            setActiveKey(num);
            handlePinInput(num);
            setTimeout(() => setActiveKey(null), 150);
        };

        return (
            <div style={styles.container}>
                <div style={styles.header}>Enter Your Access PIN</div>
                <div style={styles.pinDots}>
                    {[...Array(6)].map((_, index) => (
                        <div key={index} style={styles.dot(index >= 6 - pin.length)}></div>
                    ))}
                </div>
                <div style={styles.keypad}>
                    {[1, 2, 3, 4, 5, 6, 7, 8, 9].map((num) => (
                        <div
                            key={num}
                            style={{
                                ...styles.key,
                                ...(activeKey === num ? styles.keyActive : {}),
                            }}
                            onClick={() => handleKeyClick(num)}
                        >
                            {num}
                        </div>
                    ))}
                    <div style={{ gridColumn: '2 / 3' }}>
                        <div
                            style={{
                                ...styles.key,
                                ...(activeKey === 0 ? styles.keyActive : {}),
                            }}
                            onClick={() => handleKeyClick(0)}
                        >
                            0
                        </div>
                    </div>
                    <div style={{ gridColumn: '3 / 4' }}>
                        <div
                            style={{
                                ...styles.key,
                                ...(activeKey === 'delete' ? styles.keyActive : {}),
                            }}
                            onClick={handleDelete}
                        >
                            ⌫
                        </div>
                    </div>
                </div>
                <div style={styles.footer}>
                    <div style={styles.footerButton}>Forgot PIN</div>
                    <div style={{ width: '33%' }}></div>
                    <div style={styles.footerButton} onClick={handleCancel}>Cancel</div>
                </div>
                <div style={styles.continueButton} onClick={handlePayClick}>
                    Confirm Transfer
                </div>
            </div>
        );
    }

    // DepositPinEntry 组件
    function DepositPinEntry() {
        const styles = {
            ...PinEntry.styles, // 复用 PinEntry 的样式
            header: {
                ...PinEntry.styles.header,
                marginBottom: '20px',
            },
        };

        return (
            <div style={styles.container}>
                <div style={styles.header}>Enter Your Deposit PIN</div>
                <div style={styles.pinDots}>
                    {[...Array(6)].map((_, index) => (
                        <div key={index} style={styles.dot(index < depositPin.length)}></div>
                    ))}
                </div>
                <div style={styles.keypad}>
                    {[1, 2, 3, 4, 5, 6, 7, 8, 9].map((num) => (
                        <div
                            key={num}
                            style={{
                                ...styles.key,
                                ...(activeKey === num ? styles.keyActive : {}),
                            }}
                            onClick={() => handleDepositPinInput(num)}
                        >
                            {num}
                        </div>
                    ))}
                    <div style={{ gridColumn: '2 / 3' }}>
                        <div
                            style={{
                                ...styles.key,
                                ...(activeKey === 0 ? styles.keyActive : {}),
                            }}
                            onClick={() => handleDepositPinInput(0)}
                        >
                            0
                        </div>
                    </div>
                    <div style={{ gridColumn: '3 / 4' }}>
                        <div
                            style={{
                                ...styles.key,
                                ...(activeKey === 'delete' ? styles.keyActive : {}),
                            }}
                            onClick={handleDepositPinDelete}
                        >
                            ⌫
                        </div>
                    </div>
                </div>
                <div style={styles.footer}>
                    <div style={styles.footerButton}>Forgot PIN</div>
                    <div style={{ width: '33%' }}></div>
                    <div style={styles.footerButton} onClick={handleDepositPinCancel}>Cancel</div>
                </div>
                <div style={styles.continueButton} onClick={handleDepositSubmit}>
                    Confirm Deposit
                </div>
            </div>
        );
    }

    // DepositDetails 组件
    function DepositDetails({ navigate, setShowDepositDetails }) {
        const handleExit = () => {
            setShowDepositDetails(false);
        };

        return (
            <div style={styles.accountDetailsContainer}>
                <div style={styles.accountDetailsHeader}>
                    <div style={styles.backButton} onClick={handleExit}>←</div>
                    <div style={styles.accountDetailsTitle}>Deposit Amount</div>
                </div>
                <div style={styles.formGroup}>
                    <div style={styles.formLabel}>Amount</div>
                    <input
                        type="text"
                        style={styles.formInput}
                        value={depositAmount}
                        onChange={handleDepositAmountChange}
                    />
                </div>
                <div
                    style={{
                        ...styles.continueButton,
                        backgroundColor: depositAmount ? '#007bff' : '#e0e0e0',
                    }}
                    onClick={handleDepositContinue}
                >
                    Continue
                </div>
            </div>
        );
    }

    return (
        <div style={styles.container}>
            <div style={styles.header}>
                <div>👤</div>
                <div style={styles.headerText}>Payments</div>
                <div>🔍</div>
            </div>

            <div style={styles.quickActionsContainer}>
                <div style={styles.quickActionButton} onClick={() => setShowAccountDetails(true)}>💲 Pay</div>
                <div style={styles.quickActionButton}>🔄 Transfer</div>
                <div style={styles.quickActionButton} onClick={handleDepositClick}>🏧 Deposit</div>
            </div>

            {showPinEntry ? (
                <PinEntry />
            ) : showAccountDetails ? (
                <div style={styles.accountDetailsContainer}>
                    <div style={styles.accountDetailsHeader}>
                        <div style={styles.backButton} onClick={() => setShowAccountDetails(false)}>←</div>
                        <div style={styles.accountDetailsTitle}>Account Details</div>
                    </div>
                    <div style={styles.formGroup}>
                        <div style={styles.formLabel}>BSB</div>
                        <input type="text" style={styles.formInput} value={bsb} onChange={handleBsbChange} />
                    </div>
                    <div style={styles.formGroup}>
                        <div style={styles.formLabel}>Account Number</div>
                        <input type="text" style={styles.formInput} value={accountNumber} onChange={handleAccountNumberChange} />
                    </div>
                    <div style={styles.formGroup}>
                        <div style={styles.formLabel}>Amount</div>
                        <input type="text" style={styles.formInput} value={amount} onChange={handleAmountChange} />
                    </div>
                    <div style={styles.formGroup}>
                        <div style={styles.formLabel}>Account Name</div>
                        <input type="text" style={styles.formInput} />
                        <div style={styles.formNote}>Account name won't be matched, verified or checked with the BSB and account number.</div>
                    </div>
                    <div
                        style={styles.continueButton}
                        onClick={handleContinueClick}
                    >
                        Continue
                    </div>
                </div>
            ) : showDepositDetails ? (
                <DepositDetails 
                    navigate={navigate} 
                    setShowDepositDetails={setShowDepositDetails} 
                />
            ) : showDepositPinEntry ? (
                <DepositPinEntry />
            ) : null}

            <div style={styles.footer}>
                <div style={styles.footerItem} onClick={() => handleFooterClick('/money')}>
                    <div style={styles.footerIcon}>💵</div>
                    <div style={styles.footerText}>Money</div>
                </div>
                <div style={styles.footerItem} onClick={() => handleFooterClick('/payments')}>
                    <div style={styles.footerIcon}>💳</div>
                    <div style={styles.footerText}>Payments</div>
                </div>
                <div style={styles.footerItem} onClick={() => handleFooterClick('/card')}>
                    <div style={styles.footerIcon}>💳</div>
                    <div style={styles.footerText}>Card</div>
                </div>
                <div style={styles.footerItem} onClick={() => handleFooterClick('/support')}>
                    <div style={styles.footerIcon}>📞</div>
                    <div style={styles.footerText}>Support</div>
                </div>
            </div>
        </div>
    );
}

export default Payments;
