    function PinEntry() {
        const styles = {
            overlay: {
                position: 'fixed',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                backgroundColor: 'rgba(0, 0, 0, 0.5)',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'flex-end',
                zIndex: 1000,
            },
            container: {
                backgroundColor: '#ffffff',
                height: '60vh',
                width: '100%',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                borderRadius: '20px 20px 0 0',
                padding: '20px',
                position: 'relative',
                transition: 'bottom 0.3s ease',
            },
            header: {
                fontSize: '24px',
                fontWeight: 'bold',
                marginBottom: '30px',
            },
            pinDots: {
                display: 'flex',
                justifyContent: 'center',
                marginBottom: '30px',
            },
            keypad: {
                display: 'grid',
                gridTemplateColumns: 'repeat(3, 1fr)',
                gap: '15px',
            },
            key: {
                width: '60px',
                height: '60px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                backgroundColor: '#f0f0f0',
                borderRadius: '50%',
                fontSize: '18px',
                cursor: 'pointer',
                userSelect: 'none',
            },
            footer: {
                display: 'flex',
                justifyContent: 'space-between',
                width: '100%',
                marginTop: '20px',
            },
            footerButton: {
                fontSize: '16px',
                color: '#007bff',
                cursor: 'pointer',
                textAlign: 'center',
                width: '33%',
            },
            dot: {
                width: '10px',
                height: '10px',
                borderRadius: '50%',
                backgroundColor: '#000',
                margin: '0 5px',
                transition: 'background-color 0.2s ease-in-out',
            },
            activeDot: {
                backgroundColor: '#007bff',
            },
        };
    
        const [activeKey, setActiveKey] = useState(null);
    
        const handleKeyClick = (num) => {
            setActiveKey(num);
            handlePinInput(num);
            setTimeout(() => setActiveKey(null), 100);
        };
    
        return (
            <div style={styles.overlay}>
                <div style={styles.container}>
                    <style>{`
                        .key {
                            transition: background-color 0.1s ease-in-out;
                        }
                        .key:active {
                            background-color: #d0d0d0 !important;
                        }
                    `}</style>
                    {isLoading && <Loading />}
                    <div style={styles.header}>Enter Your Access PIN</div>
                    <div style={styles.pinDots}>
                        {[...Array(6)].map((_, index) => (
                            <div 
                                key={index} 
                                style={{
                                    ...styles.dot,
                                    ...(index >= 6 - pin.length ? styles.activeDot : {})
                                }}
                            ></div>
                        ))}
                    </div>
                    <div style={styles.keypad}>
                        {[1, 2, 3, 4, 5, 6, 7, 8, 9, 0].map((num) => (
                            <div
                                key={num}
                                className="key"
                                style={{
                                    ...styles.key,
                                    backgroundColor: activeKey === num ? '#d0d0d0' : '#f0f0f0',
                                }}
                                onMouseDown={() => setActiveKey(num)}
                                onMouseUp={() => setActiveKey(null)}
                                onMouseLeave={() => setActiveKey(null)}
                                onClick={() => handleKeyClick(num)}
                            >
                                {num}
                            </div>
                        ))}
                        <div style={{ gridColumn: '3 / 4' }}>
                            <div
                                className="key"
                                style={{
                                    ...styles.key,
                                    backgroundColor: activeKey === 'delete' ? '#d0d0d0' : '#f0f0f0',
                                }}
                                onMouseDown={() => setActiveKey('delete')}
                                onMouseUp={() => setActiveKey(null)}
                                onMouseLeave={() => setActiveKey(null)}
                                onClick={() => {
                                    handleDelete();
                                }}
                            >
                                ⌫
                            </div>
                        </div>
                    </div>
                    <div style={styles.footer}>
                        <div style={styles.footerButton}>Forgot PIN</div>
                        <div style={{ width: '33%' }}></div>
                        <div style={styles.footerButton} onClick={handleCancel}>Cancel</div>
                    </div>
                </div>
            </div>
        );
    }
    function DepositDetails({ navigate, setShowDepositDetails }) {
        React.useEffect(() => {
            if (depositAmountRef.current) {
                depositAmountRef.current.focus();
            }
        }, []);
    
        const handleExit = () => {
            setShowDepositDetails(false);
            setDepositAmount('');
        };
    
        const isAmountValid = depositAmount && parseFloat(depositAmount) > 0;
    
        const buttonStyle = {
            ...styles.continueButton,
            backgroundColor: isAmountValid ? '#007bff' : '#e0e0e0',
            color: isAmountValid ? '#fff' : '#999',
            cursor: isAmountValid ? 'pointer' : 'not-allowed',
        };
    
        return (
            <div style={styles.depositDetailsContainer}>
                <div style={styles.accountDetailsHeader}>
                    <div style={styles.backButton} onClick={handleExit}>←</div>
                    <div style={styles.accountDetailsTitle}>Deposit Amount</div>
                </div>
                <div style={styles.formGroup}>
                    <div style={styles.formLabel}>Amount</div>
                    <input 
                        type="text" 
                        style={styles.formInput} 
                        value={depositAmount} 
                        onChange={handleDepositAmountChange} 
                        ref={depositAmountRef}
                    />
                </div>
                <div
                    style={buttonStyle}
                    onClick={isAmountValid ? handleDepositContinue : undefined}
                >
                    Continue
                </div>
            </div>
        );
    }

    function DepositPinEntry() {
        const styles = {
            container: {
                backgroundColor: '#dcdcdc',
                height: '60vh',
                width: '100vw',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                borderRadius: '20px 20px 0 0',
                padding: '20px',
                position: 'fixed',
                bottom: '0',
                left: '0',
                transition: 'bottom 0.3s ease',
            },
            header: {
                fontSize: '24px',
                fontWeight: 'bold',
                marginBottom: '30px',
            },
            pinDots: {
                display: 'flex',
                justifyContent: 'center',
                marginBottom: '30px',
            },
            dot: {
                width: '10px',
                height: '10px',
