            keypad: {
                display: 'grid',
                gridTemplateColumns: 'repeat(3, 1fr)',
                gap: '15px',
            },
            key: {
                width: '60px',
                height: '60px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                backgroundColor: '#e0e0e0',
                borderRadius: '50%',
                fontSize: '18px',
                cursor: 'pointer',
                transition: 'transform 0.1s ease-in-out',
            },
            keyActive: {
                transform: 'scale(0.9)',
            },
            footer: {
                display: 'flex',
                justifyContent: 'space-between',
                width: '100%',
