import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Payments() {
    const [showAccountDetails, setShowAccountDetails] = useState(false);
    const [showDepositDetails, setShowDepositDetails] = useState(false);
    const [showPinEntry, setShowPinEntry] = useState(false);
    const [bsb, setBsb] = useState('');
    const [accountNumber, setAccountNumber] = useState('');
    const [pin, setPin] = useState('');
    const [amount, setAmount] = useState('');
    const [animatePinEntry, setAnimatePinEntry] = useState(false);
    const [depositAmount, setDepositAmount] = useState('');
    const [showDepositPinEntry, setShowDepositPinEntry] = useState(false);
    const [depositPin, setDepositPin] = useState('');

    const depositAmountRef = useRef(null);

    const navigate = useNavigate();

    const isContinueEnabled = bsb.replace(/\s+/g, '').length === 6 && accountNumber.replace(/\s+/g, '').length === 9;

    const formatNumberWithSpaces = (value) => {
        return value.replace(/\D/g, '') // Remove all non-digit characters
            .replace(/(\d{3})(?=\d)/g, '$1 '); // Add a space every three digits
    };

    const handleBsbChange = (e) => {
        const formattedValue = formatNumberWithSpaces(e.target.value);
        setBsb(formattedValue);
    };

    const handleAccountNumberChange = (e) => {
        const formattedValue = formatNumberWithSpaces(e.target.value);
        setAccountNumber(formattedValue);
    };

    const handleAmountChange = (e) => {
        setAmount(e.target.value);
    };

    const handleContinueClick = () => {
        if (isContinueEnabled) {
            setAnimatePinEntry(true);
            setTimeout(() => setShowPinEntry(true), 300);
            setShowAccountDetails(false);
        }
    };

    const handlePinInput = (num) => {
        if (pin.length < 6) {
            setPin((prevPin) => prevPin + num);
        }
    };

    const handleDelete = () => {
        setPin((prevPin) => prevPin.slice(0, -1));
    };

    const handleCancel = () => {
        setAnimatePinEntry(false);
        setTimeout(() => setShowPinEntry(false), 300);
    };

    const handleFooterClick = (path) => {
        navigate(path);
    };

    const handlePayClick = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            return;
        }

        try {
            const response = await axios.post(
                'http://localhost:5128/api/TransferRequest/create',
                {
                    BSB: bsb.replace(/\s+/g, ''), // 移除空格
                    AccountNumber: accountNumber.replace(/\s+/g, ''), // 移除空格
                    Amount: parseFloat(amount), // 转换为数字
                    Pin: pin,
                },
                {
                    headers: {
                        Authorization: token,
                    },
                }
            );

            if (response.status === 200) {
                alert('Transfer request created successfully!');
            }
        } catch (error) {
            console.error('Error creating transfer request:', error);
            alert('Failed to create transfer request. Please check your inputs and try again.');
        } finally {
            setPin(''); // 清空PIN
            setShowPinEntry(false); // 关闭PIN输入界面
            setAnimatePinEntry(false); // 停止动画
        }
    };


    const handleDepositAmountChange = (e) => {
        const value = e.target.value;
        // 只允许数字和小数点，且小数点后最多两位
        const regex = /^\d*\.?\d{0,2}$/;
        if (regex.test(value) || value === '') {
            setDepositAmount(value);
        }
    };

        // New deposit functions
    const handleDepositClick = () => {
        setShowDepositDetails(true);
        setShowAccountDetails(false);
    };



    React.useEffect(() => {
        if (depositAmountRef.current) {
            depositAmountRef.current.focus();
        }
    }, [showDepositDetails]);


    const handleDepositContinue = () => {
        if (depositAmount && parseFloat(depositAmount) > 0) {
            setShowDepositPinEntry(true);
            setShowDepositDetails(false);
        } else {
            alert('Please enter a valid amount.');
        }
    };


    const handleDepositPinInput = (num) => {
        if (depositPin.length < 6) {
            setDepositPin((prevPin) => prevPin + num);
        }
    };


    const handleDepositPinDelete = () => {
        setDepositPin((prevPin) => prevPin.slice(0, -1));
    };

    const handleDepositPinCancel = () => {
        setShowDepositPinEntry(false);
        setDepositPin('');
    };


    const handleDepositSubmit = async () => {
        if (depositPin.length !== 6) {
            alert('Please enter a 6-digit PIN.');
            return;
        }

        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            return;
        }

        try {
            const response = await axios.post(
                'http://localhost:5128/api/TransferRequest/deposit',
                {
                    Amount: parseFloat(depositAmount),
                    Pin: depositPin,
                },
                {
                    headers: {
                        Authorization: token,
                    },
                }
            );

            if (response.status === 200) {
                alert('Deposit successful!');
                setShowDepositPinEntry(false);
                setDepositPin('');
                setDepositAmount('');
            }
        } catch (error) {
            console.error('Error during deposit:', error);
            alert('Failed to make deposit. Please try again.');
        }
    };

    const styles = {
        container: {
            backgroundColor: '#f4f4f4',
            minHeight: '100vh',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            width: '100%',
            boxSizing: 'border-box',
            padding: 0,
            overflowX: 'hidden',
        },
        header: {
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            width: '100%',
            marginBottom: '20px',
            boxSizing: 'border-box',
            padding: '10px',
        },

        depositDetailsContainer: {
            position: 'fixed',
            bottom: '0',
            left: 0,
            width: '100%',
            height: '80%',
            backgroundColor: '#fff',
            borderTopLeftRadius: '20px',
            borderTopRightRadius: '20px',
            boxShadow: '0 -2px 8px rgba(0, 0, 0, 0.1)',
            transition: 'bottom 0.3s ease',
            padding: '20px',
            zIndex: 1000,
        },


        headerText: {
            fontSize: '24px',
            fontWeight: 'bold',
        },
        quickActionsContainer: {
            display: 'flex',
            justifyContent: 'space-around',
            width: '60%',
            marginBottom: '20px',
        },
        quickActionButton: {
            backgroundColor: '#007bff',
            color: '#fff',
            padding: '15px 30px',
            borderRadius: '25px',
            textAlign: 'center',
            fontSize: '18px',
            cursor: 'pointer',
            flex: 1,
            margin: '0 10px',
        },
        quickPayContainer: {
            width: '90%',
            marginBottom: '20px',
        },
        quickPayHeader: {
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '10px',
        },
        quickPayHeader1: {
            fontSize: '25px',
            fontWeight: 'bold',
        },
        quickPayHeader2: {
            fontSize: '19px',
            color: '#007bff',
            cursor: 'pointer',
        },
        quickPayItems: {
            display: 'flex',
            justifyContent: 'space-around',
            alignItems: 'center',
        },
        quickPayItem: {
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            cursor: 'pointer',
        },
        quickPayIcon: {
            width: '60px',
            height: '60px',
            borderRadius: '50%',
            backgroundColor: '#ADD8E6',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            color: '#0066CC',
            fontSize: '24px',
            marginBottom: '5px',
        },
        scheduledContainer: {
            width: '90%',
            backgroundColor: '#fff',
            borderRadius: '10px',
            padding: '15px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
            marginBottom: '20px',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
        },
        failedContainer: {
            width: '90%',
            backgroundColor: '#fff',
            borderRadius: '10px',
            padding: '15px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
            marginBottom: '20px',
            borderLeft: '5px solid red',
        },
        transactionList: {
            width: '90%',
            backgroundColor: '#fff',
            borderRadius: '10px',
            padding: '15px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
            marginBottom: '20px',
            boxSizing: 'border-box',
            margin: '0 20px',
        },
        transactionItem: {
            display: 'flex',
            justifyContent: 'space-between',
            marginBottom: '10px',
        },
        footer: {
            display: 'flex',
            justifyContent: 'space-around',
            width: '100%',
            backgroundColor: '#fff',
            padding: '10px 0',
            boxShadow: '0 -2px 8px rgba(0, 0, 0, 0.1)',
            marginTop: 'auto',
            boxSizing: 'border-box',
        },
        footerItem: {
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '12px',
            cursor: 'pointer',
        },
        footerIcon: {
            fontSize: '24px',
            color: '#007bff',
            marginBottom: '5px',
        },
        accountDetailsContainer: {
            position: 'fixed',
            bottom: showAccountDetails || showDepositDetails ? '0' : '-100%',
            left: 0,
            width: '100%',
            height: '80%',
            backgroundColor: '#fff',
            borderTopLeftRadius: '20px',
            borderTopRightRadius: '20px',
            boxShadow: '0 -2px 8px rgba(0, 0, 0, 0.1)',
            transition: 'bottom 0.3s ease',
            padding: '20px',
            zIndex: 1000,
        },
        accountDepositContainer: {
            position: 'fixed',
            bottom: showAccountDetails ? '0' : '-100%',
            left: 0,
            width: '100%',
            height: '80%',
            backgroundColor: '#fff',
            borderTopLeftRadius: '20px',
            borderTopRightRadius: '20px',
            boxShadow: '0 -2px 8px rgba(0, 0, 0, 0.1)',
            transition: 'bottom 0.3s ease',
            padding: '20px',
            zIndex: 1000,
        },
        accountDetailsHeader: {
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '20px',
        },
        backButton: {
            fontSize: '24px',
            color: '#007bff',
            cursor: 'pointer',
            flexShrink: 0,
            marginRight: '10px', // 添加一些右边距

        },
        accountDetailsTitle: {
            fontSize: '20px',
            fontWeight: 'bold',
            textAlign: 'center',
            flex: 1,
        },
        formGroup: {
            marginBottom: '15px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'flex-start',
        },
        formLabel: {
            marginBottom: '5px',
            fontSize: '16px',
            fontWeight: 'bold',
        },
        formInput: {
            width: 'calc(100% - 40px)', // Ensure input field does not exceed container width
            padding: '10px',
            fontSize: '16px',
            borderRadius: '5px',
            border: '1px solid #ccc',
            boxSizing: 'border-box',
        },
        formNote: {
            fontSize: '12px',
            color: '#999',
            marginTop: '5px',
        },
        continueButton: {
            width: '30%',
            padding: '15px',
            borderRadius: '25px',
            backgroundColor: isContinueEnabled ? '#007bff' : '#e0e0e0',
            textAlign: 'center',
            fontSize: '18px',
            color: isContinueEnabled ? '#fff' : '#999',
            cursor: isContinueEnabled ? 'pointer' : 'not-allowed',
            marginTop: '20px',
            marginLeft: 'auto',
            marginRight: 'auto', // Center the button horizontally
            display: 'block', // Ensure the button is a block element
        },


        footer: {
            display: 'flex',
            justifyContent: 'space-around',
            width: '100%',
            backgroundColor: '#fff',
            padding: '10px 0',
            boxShadow: '0 -2px 8px rgba(0, 0, 0, 0.1)',
            marginTop: 'auto',
            boxSizing: 'border-box',
        },
        footerItem: {
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '12px',
            cursor: 'pointer',
        },
        footerIcon: {
            fontSize: '24px',
            color: '#007bff',
            marginBottom: '5px',
        },
    };

    function PinEntry() {
        const styles = {
            container: {
                backgroundColor: '#dcdcdc', // Differentiate from main background color
                height: '60vh', // Height covering 60% from bottom to top
                width: '100vw', // Cover entire screen width
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                borderRadius: '20px 20px 0 0', // Top corners rounded
                padding: '20px',
                position: 'fixed',
                bottom: animatePinEntry ? '0' : '-100%',
                left: '0',
                transition: 'bottom 0.3s ease', // Smooth transition
            },
            header: {
                fontSize: '24px',
                fontWeight: 'bold',
                marginBottom: '30px', // Increase space between header and PIN input
            },
            pinDots: {
                display: 'flex',
                justifyContent: 'center',
                marginBottom: '30px',
            },
            dot: (isActive) => ({
                width: '10px',
                height: '10px',
                borderRadius: '50%',
                backgroundColor: isActive ? '#007bff' : '#000',
                margin: '0 5px',
            }),
            keypad: {
                display: 'grid',
                gridTemplateColumns: 'repeat(3, 1fr)',
                gap: '15px',
            },
            key: {
                width: '60px',
                height: '60px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                backgroundColor: '#e0e0e0',
                borderRadius: '50%',
                fontSize: '18px',
                cursor: 'pointer',
                transition: 'background-color 0.2s',
            },
            keyActive: {
                backgroundColor: '#cccccc', // Change color on key press
            },
            footer: {
                display: 'flex',
                justifyContent: 'space-between',
                width: '100%',
                marginTop: '20px',
            },
            footerButton: {
                fontSize: '16px',
                color: '#007bff',
                cursor: 'pointer',
                textAlign: 'center',
                width: '33%', // Make buttons take up 1/3 of the width
            },
        };

        const [activeKey, setActiveKey] = useState(null);

        const handleKeyClick = (num) => {
            setActiveKey(num);
            handlePinInput(num);
            setTimeout(() => setActiveKey(null), 150); // Brief click effect
        };
    }


    function DepositDetails({ navigate, setShowDepositDetails }) {
        const handleExit = () => {
            setShowDepositDetails(false);
        };

    
        return (
            <div style={styles.depositDetailsContainer}>
                <div style={styles.accountDetailsHeader}>
                    <div style={styles.backButton} onClick={handleExit}>←</div>
                    <div style={styles.accountDetailsTitle}>Deposit Amount</div>
                </div>
                <div style={styles.formGroup}>
                    <div style={styles.formLabel}>Amount</div>
                    <input 
                        type="text" 
                        style={styles.formInput} 
                        value={depositAmount} 
                        onChange={handleDepositAmountChange} 
                    />
                </div>
                <div
                    style={{...styles.continueButton, backgroundColor: depositAmount ? '#007bff' : '#e0e0e0'}}
                    onClick={handleDepositContinue}
                >
                    Continue
                </div>
            </div>
            );
        }

    function DepositPinEntry() {
        const styles = {
            // 复制 PinEntry 组件的样式
            container: {
                backgroundColor: '#dcdcdc',
                height: '60vh',
                width: '100vw',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                borderRadius: '20px 20px 0 0',
                padding: '20px',
                position: 'fixed',
                bottom: '0',
                left: '0',
                transition: 'bottom 0.3s ease',
            },
            // ... 复制其他样式
        };
        const [activeKey, setActiveKey] = useState(null);
        const handleKeyClick = (num) => {
            setActiveKey(num);
            handleDepositPinInput(num);
            setTimeout(() => setActiveKey(null), 150);
        };
        return (
            <div style={styles.container}>
                <div style={styles.header}>Enter Your Deposit PIN</div>
                <div style={styles.pinDots}>
                    {[...Array(6)].map((_, index) => (
                        <div key={index} style={styles.dot(index < depositPin.length)}></div>
                    ))}
                </div>
                <div style={styles.keypad}>
                    {[1, 2, 3, 4, 5, 6, 7, 8, 9].map((num) => (
                        <div
                            key={num}
                            style={{
                                ...styles.key,
                                ...(activeKey === num ? styles.keyActive : {}),
                            }}
                            onClick={() => handleKeyClick(num)}
                        >
                            {num}
                        </div>
                    ))}
                    <div style={{ gridColumn: '2 / 3' }}>
                        <div
                            style={{
                                ...styles.key,
                                ...(activeKey === 0 ? styles.keyActive : {}),
                            }}
                            onClick={() => handleKeyClick(0)}
                        >
                            0
                        </div>
                    </div>
                    <div style={{ gridColumn: '3 / 4' }}>
                        <div
                            style={{
                                ...styles.key,
                                ...(activeKey === 'delete' ? styles.keyActive : {}),
                            }}
                            onClick={handleDepositPinDelete}
                        >
                            ⌫
                        </div>
                    </div>
                </div>
                <div style={styles.footer}>
                    <div style={styles.footerButton}>Forgot PIN</div>
                    <div style={{ width: '33%' }}></div>
                    <div style={styles.footerButton} onClick={handleDepositPinCancel}>Cancel</div>
                </div>
                <div style={styles.continueButton} onClick={handleDepositSubmit}>
                    Confirm Deposit
                </div>
            </div>
        );
    }


    return (
        <div style={styles.container}>
            <div style={styles.header}>
                <div>👤</div>
                <div style={styles.headerText}>Payments</div>
                <div>🔍</div>
            </div>

            <div style={styles.quickActionsContainer}>
                <div style={styles.quickActionButton} onClick={() => setShowAccountDetails(true)}>💲 Pay</div>
                <div style={styles.quickActionButton}>🔄 Transfer</div>
                <div style={styles.quickActionButton} onClick={() => setShowDepositDetails(true)}>🏧 Deposit</div>
            </div>

            {showPinEntry ? (
                <PinEntry />
            ) : showAccountDetails ? (
                <div style={styles.accountDetailsContainer}>
                    <div style={styles.accountDetailsHeader}>
                        <div style={styles.backButton} onClick={() => setShowAccountDetails(false)}>←</div>
                        <div style={styles.accountDetailsTitle}>Account Details</div>
                    </div>

                    <div style={styles.formGroup}>
                        <div style={styles.formLabel}>BSB</div>
                        <input type="text" style={styles.formInput} value={bsb} onChange={handleBsbChange} />
                    </div>
                    <div style={styles.formGroup}>
                        <div style={styles.formLabel}>Account Number</div>
                        <input type="text" style={styles.formInput} value={accountNumber} onChange={handleAccountNumberChange} />
                    </div>
                    <div style={styles.formGroup}>
                        <div style={styles.formLabel}>Amount</div>
                        <input type="text" style={styles.formInput} value={amount} onChange={handleAmountChange} />
                    </div>
                    <div style={styles.formGroup}>
                        <div style={styles.formLabel}>Account Name</div>
                        <input type="text" style={styles.formInput} />
                        <div style={styles.formNote}>Account name won't be matched, verified or checked with the BSB and account number.</div>
                    </div>

                    <div
                        style={styles.continueButton}
                        onClick={handleContinueClick}
                    >
                        Continue
                    </div>
                </div>
            ) : showDepositDetails ? (
                <DepositDetails 
                    navigate={navigate} 
                    setShowDepositDetails={setShowDepositDetails} 
                />
            ) : showDepositPinEntry ? (
                <DepositPinEntry />
            ) : null}

            <div style={styles.footer}>
                <div style={styles.footerItem} onClick={() => handleFooterClick('/money')}>
                    <div style={styles.footerIcon}>💵</div>
                    <div style={styles.footerText}>Money</div>
                </div>
                <div style={styles.footerItem} onClick={() => handleFooterClick('/payments')}>
                    <div style={styles.footerIcon}>💳</div>
                    <div style={styles.footerText}>Payments</div>
                </div>
                <div style={styles.footerItem} onClick={() => handleFooterClick('/card')}>
                    <div style={styles.footerIcon}>💳</div>
                    <div style={styles.footerText}>Card</div>
                </div>
                <div style={styles.footerItem} onClick={() => handleFooterClick('/support')}>
                    <div style={styles.footerIcon}>📞</div>
                    <div style={styles.footerText}>Support</div>
                </div>
            </div>
            </div>
    );
}

export default Payments;
