import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Payments() {
    const [showAccountDetails, setShowAccountDetails] = useState(false);
    const [showDepositDetails, setShowDepositDetails] = useState(false);
    const [showPinEntry, setShowPinEntry] = useState(false);
    const [bsb, setBsb] = useState('');
    const [accountNumber, setAccountNumber] = useState('');
    const [pin, setPin] = useState('');
    const [amount, setAmount] = useState(''); // 管理 amount 状态
    const [animatePinEntry, setAnimatePinEntry] = useState(false);

    const navigate = useNavigate();

    const isContinueEnabled = amount.trim() !== ''; // 确保金额已输入

    const formatNumberWithSpaces = (value) => {
        return value.replace(/\D/g, '') // 移除所有非数字字符
            .replace(/(\d{3})(?=\d)/g, '$1 '); // 每三位数字后添加一个空格
    };

    const handleBsbChange = (e) => {
        const formattedValue = formatNumberWithSpaces(e.target.value);
        setBsb(formattedValue);
    };

    const handleAccountNumberChange = (e) => {
        const formattedValue = formatNumberWithSpaces(e.target.value);
        setAccountNumber(formattedValue);
    };

    const handleAmountChange = (e) => {
        setAmount(e.target.value);
    };

    const handleContinueClick = () => {
        if (isContinueEnabled) {
            setAnimatePinEntry(true);
            setTimeout(() => setShowPinEntry(true), 300);
            setShowAccountDetails(false);
        }
    };

    const handleDepositClick = () => {
        if (isContinueEnabled) {
            setAnimatePinEntry(true);
            setTimeout(() => setShowPinEntry(true), 300);
            setShowDepositDetails(false);
        }
    };

    const handlePinInput = (num) => {
        if (pin.length < 6) {
            setPin((prevPin) => prevPin + num);
        }
    };

    const handleDelete = () => {
        setPin((prevPin) => prevPin.slice(0, -1));
    };

    const handleCancel = () => {
        setAnimatePinEntry(false);
        setTimeout(() => setShowPinEntry(false), 300);
    };

    const handleFooterClick = (path) => {
        navigate(path);
    };

    const handlePayClick = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            return;
        }

        try {
            const response = await axios.post(
                'http://localhost:5128/api/TransferRequest/create',
                {
                    BSB: bsb.replace(/\s+/g, ''), // 移除空格
                    AccountNumber: accountNumber.replace(/\s+/g, ''), // 移除空格
                    Amount: parseFloat(amount), // 转换为数字
                    Pin: pin,
                },
                {
                    headers: {
                        Authorization: token,
                    },
                }
            );

            if (response.status === 200) {
                alert('Transfer request created successfully!');
            }
        } catch (error) {
            console.error('Error creating transfer request:', error);
            alert('Failed to create transfer request. Please check your inputs and try again.');
        } finally {
            setPin(''); // 清空PIN
            setShowPinEntry(false); // 关闭PIN输入界面
            setAnimatePinEntry(false); // 停止动画
        }
    };

    const handleDepositSubmit = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            return;
        }

        try {
            const response = await axios.post(
                'http://localhost:5128/api/TransferRequest/deposit',
                {
                    Amount: parseFloat(amount), // 转换为数字
                    Pin: pin,
                },
                {
                    headers: {
                        Authorization: token,
                    },
                }
            );

            if (response.status === 200) {
                alert('Deposit successful!');
            }
        } catch (error) {
            console.error('Error during deposit:', error);
            alert('Failed to complete deposit. Please check your inputs and try again.');
        } finally {
            setPin(''); // 清空PIN
            setShowPinEntry(false); // 关闭PIN输入界面
            setAnimatePinEntry(false); // 停止动画
        }
    };

    const styles = {
        container: {
            backgroundColor: '#f4f4f4',
            minHeight: '100vh',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            width: '100%',
            boxSizing: 'border-box',
            padding: 0,
            overflowX: 'hidden',
        },
        header: {
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            width: '100%',
            marginBottom: '20px',
            boxSizing: 'border-box',
            padding: '10px',
        },
        headerText: {
            fontSize: '24px',
            fontWeight: 'bold',
        },
        quickActionsContainer: {
            display: 'flex',
            justifyContent: 'space-around',
            width: '60%',
            marginBottom: '20px',
        },
        quickActionButton: {
            backgroundColor: '#007bff',
            color: '#fff',
            padding: '15px 30px',
            borderRadius: '25px',
            textAlign: 'center',
            fontSize: '18px',
            cursor: 'pointer',
            flex: 1,
            margin: '0 10px',
        },
        accountDetailsContainer: {
            position: 'fixed',
            bottom: showAccountDetails ? '0' : '-100%',
            left: 0,
            width: '100%',
            height: '80%',
            backgroundColor: '#fff',
            borderTopLeftRadius: '20px',
            borderTopRightRadius: '20px',
            boxShadow: '0 -2px 8px rgba(0, 0, 0, 0.1)',
            transition: 'bottom 0.3s ease',
            padding: '20px',
            zIndex: 1000,
        },
        accountDepositContainer: {
            position: 'fixed',
            bottom: showDepositDetails ? '0' : '-100%',
            left: 0,
            width: '100%',
            height: '80%',
            backgroundColor: '#fff',
            borderTopLeftRadius: '20px',
            borderTopRightRadius: '20px',
            boxShadow: '0 -2px 8px rgba(0, 0, 0, 0.1)',
            transition: 'bottom 0.3s ease',
            padding: '20px',
            zIndex: 1000,
        },
        accountDetailsHeader: {
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '20px',
        },
        backButton: {
            fontSize: '24px',
            color: '#007bff',
            cursor: 'pointer',
            flexShrink: 0,
        },
        accountDetailsTitle: {
            fontSize: '20px',
            fontWeight: 'bold',
            textAlign: 'center',
            flex: 1,
        },
        formGroup: {
            marginBottom: '15px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'flex-start',
        },
        formLabel: {
            marginBottom: '5px',
            fontSize: '16px',
            fontWeight: 'bold',
        },
        formInput: {
            width: 'calc(100% - 40px)', // 确保输入框不会超过容器宽度
            padding: '10px',
            fontSize: '16px',
            borderRadius: '5px',
            border: '1px solid #ccc',
            boxSizing: 'border-box',
        },
        formNote: {
            fontSize: '12px',
            color: '#999',
            marginTop: '5px',
        },
        continueButton: {
            width: '30%',
            padding: '15px',
            borderRadius: '25px',
            backgroundColor: isContinueEnabled ? '#007bff' : '#e0e0e0',
            textAlign: 'center',
            fontSize: '18px',
            color: isContinueEnabled ? '#fff' : '#999',
            cursor: isContinueEnabled ? 'pointer' : 'not-allowed',
            marginTop: '20px',
            marginLeft: 'auto',
            marginRight: 'auto', // 水平居中按钮
            display: 'block', // 确保按钮是块级元素
        },
    };

    function PinEntry({ onConfirm }) {
        const styles = {
            container: {
                backgroundColor: '#dcdcdc', // 与主背景色区分开
                height: '60vh', // 从底部到顶部覆盖60%的高度
                width: '100vw', // 覆盖整个屏幕宽度
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                borderRadius: '20px 20px 0 0', // 顶部角落圆角
                padding: '20px',
                position: 'fixed',
                bottom: animatePinEntry ? '0' : '-100%',
                left: '0',
                transition: 'bottom 0.3s ease', // 平滑过渡
            },
            header: {
                fontSize: '24px',
                fontWeight: 'bold',
                marginBottom: '30px', // 增加标题和PIN输入之间的空间
            },
            pinDots: {
                display: 'flex',
                justifyContent: 'center',
                marginBottom: '30px',
            },
            dot: (isActive) => ({
                width: '10px',
                height: '10px',
                borderRadius: '50%',
                backgroundColor: isActive ? '#007bff' : '#000',
                margin: '0 5px',
            }),
            keypad: {
                display: 'grid',
                gridTemplateColumns: 'repeat(3, 1fr)',
                gap: '15px',
            },
            key: {
                width: '60px',
                height: '60px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                backgroundColor: '#e0e0e0',
                borderRadius: '50%',
                fontSize: '18px',
                cursor: 'pointer',
                transition: 'background-color 0.2s',
            },
            keyActive: {
                backgroundColor: '#cccccc', // 按键按下时改变颜色
            },
            footer: {
                display: 'flex',
                justifyContent: 'space-between',
                width: '100%',
                marginTop: '20px',
            },
            footerButton: {
                fontSize: '16px',
                color: '#007bff',
                cursor: 'pointer',
                textAlign: 'center',
                width: '33%', // 使按钮占用宽度的1/3
            },
        };

        const [activeKey, setActiveKey] = useState(null);

        const handleKeyClick = (num) => {
            setActiveKey(num);
            handlePinInput(num);
            setTimeout(() => setActiveKey(null), 150); // 短暂的点击效果
        };

        return (
            <div style={styles.container}>
                <div style={styles.header}>Enter Your Access PIN</div>
                <div style={styles.pinDots}>
                    {[...Array(6)].map((_, index) => (
                        <div key={index} style={styles.dot(index >= 6 - pin.length)}></div>
                    ))}
                </div>
                <div style={styles.keypad}>
                    {[1, 2, 3, 4, 5, 6, 7, 8, 9].map((num) => (
                        <div
                            key={num}
                            style={{
                                ...styles.key,
                                ...(activeKey === num ? styles.keyActive : {}),
                            }}
                            onClick={() => handleKeyClick(num)}
                        >
                            {num}
                        </div>
                    ))}
                    <div style={{ gridColumn: '2 / 3' }}>
                        <div
                            style={{
                                ...styles.key,
                                ...(activeKey === 0 ? styles.keyActive : {}),
                            }}
                            onClick={() => handleKeyClick(0)}
                        >
                            0
                        </div>
                    </div>
                    <div style={{ gridColumn: '3 / 4' }}>
                        <div
                            style={{
                                ...styles.key,
                                ...(activeKey === 'delete' ? styles.keyActive : {}),
                            }}
                            onClick={handleDelete}
                        >
                            ⌫
                        </div>
                    </div>
                </div>
                <div style={styles.footer}>
                    <div style={styles.footerButton}>Forgot PIN</div>
                    <div style={{ width: '33%' }}></div> {/* 按钮之间的空白 */}
                    <div style={styles.footerButton} onClick={handleCancel}>Cancel</div>
                </div>
                <div style={styles.continueButton} onClick={onConfirm}>
                    Confirm Deposit
                </div>
            </div>
        );
    }

    return (
        <div style={styles.container}>
            <div style={styles.header}>
                <div>👤</div>
                <div style={styles.headerText}>Payments</div>
                <div>🔍</div>
            </div>

            <div style={styles.quickActionsContainer}>
                <div style={styles.quickActionButton} onClick={() => setShowAccountDetails(true)}>💲 Pay</div>
                <div style={styles.quickActionButton}>🔄 Transfer</div>
                <div style={styles.quickActionButton} onClick={() => setShowDepositDetails(true)}>🏧 Deposit</div>
            </div>

            {showDepositDetails && (
                <div style={styles.accountDepositContainer}>
                    <div style={styles.formGroup}>
                        <div style={styles.formLabel}>Amount</div>
                        <input type="text" style={styles.formInput} value={amount} onChange={handleAmountChange} />
                    </div>
                    <PinEntry onConfirm={handleDepositSubmit} />
                </div>
            )}

            <div style={styles.footer}>
                <div style={styles.footerItem} onClick={() => handleFooterClick('/money')}>
                    <div style={styles.footerIcon}>💵</div>
                    <div style={styles.footerText}>Money</div>
                </div>
                <div style={styles.footerItem} onClick={() => handleFooterClick('/payments')}>
                    <div style={styles.footerIcon}>💳</div>
                    <div style={styles.footerText}>Payments</div>
                </div>
                <div style={styles.footerItem} onClick={() => handleFooterClick('/card')}>
                    <div style={styles.footerIcon}>💳</div>
                    <div style={styles.footerText}>Card</div>
                </div>
                <div style={styles.footerItem} onClick={() => handleFooterClick('/support')}>
                    <div style={styles.footerIcon}>📞</div>
                    <div style={styles.footerText}>Support</div>
                </div>
            </div>
        </div>
    );
}

export default Payments;
